import React, { useState, useEffect } from 'react';
import { KeyboardAvoidingView, Button, StyleSheet, TextInput, TouchableOpacity } from 'react-native';

import { auth } from '../storage/firebase';
import { Text, View } from '../components/Themed';

export default function LoginScreen({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const onUnmount = auth.onAuthStateChanged(user => {
      if (user) {
        navigation.navigate("Farmer Profile", { farmer: user.displayName });
      }
    });

    return onUnmount;
  });

  return (
    <KeyboardAvoidingView style={styles.container}>
      <View style={styles.inputSection}>
        <TextInput
          placeholder="Username"
          onChangeText={setUsername}
          value={username}
          style={styles.input}
        />
        <TextInput
          placeholder="password"
          onChangeText={setPassword}
          value={password}
          style={styles.input}
          secureTextEntry
        />
      </View>

      <View style={styles.buttonSection}>
        <Button
          style={[styles.button]}
          title="Login"
        />
        <Button
          style={[styles.button, styles.buttonOutline]}
          title="Register"
        />
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  inputSection: {
    width: '80%',
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 10,
    marginBottom: 5,
  },
  buttonSection: {
    width: '60%',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 35,
  },
  button: {
    backgroundColor: '#0782f9',
    width: '100%',
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
    marginBottom: 5,
  },
  buttonOutline: {
    backgroundColor: '#000',
    borderColor: '#0782f9',
    borderWidth: 2,
  }
});
