import Airtable from 'airtable'
import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import type { RootState } from '../../app/store'

import type { Farmer } from './types';
import Images from './assets/Images';


function readInitialState() {
  let farmers: Array<Farmer> = [];

let base = new Airtable({apiKey: process.env.AIRTABLE_API_KEY})
  .base('appwPdl5QXUtRh8Rz');
base('Table 1').select({}).eachPage(
  async function page(records, fetchNextPage) {
    records.map(record => {
      return {
        Name: record.get('Name'),
        Image: '',
        Username: record.get('Username'),
        Location: record.get('Location'),
        Favorites: record.get('Favorites').split(',')
      };
    })
    .forEach(m => farmesr.push(m));
    fetchNextPage();
  },
  async function done(err) {
    console.log(err);
    console.log(farmers);
  });
  return farmers;
}

const initialState: Array<Farmer> = readInitialState();
const v = 
[
  {
    Name: 'Yuki Sano',
    Image: Images.Farmer1,
    Username: 'yusano',
    Location: 'Nagano', 
    Favorites: [ 'cabbage', 'spinach']
  },
  {
    Name: 'Yuichiro Watanabe',
    Image: Images.Farmer2,
    Username: 'yuichinabe',
    Location: 'Niigata',
    Favorites: [ 'rice', 'pumpkin']
  },
  {
    Name: 'Megumi Yamada',
    Image: Images.Farmer3,
    Username: 'watermelon',
    Location: 'Yamagata',
    Favorites: [ 'garlic', 'onion']
  },
];

export const farmerSlice = createSlice({
  name: 'farmer',
  initialState,
  reducers: {
    append: (state, farmer) => {
      console.log(farmer);
      state.push(farmer.payload);
    },
    update: (state, username, farmer) => {
      for (var i =0; i < state.length; ++i) {
        if (state[i].Username == username) {
          state[i] = {
            ...farmer,
            ...state[i],
          }
        }
      }
    },
  },
});

export const { append, update } = farmerSlice.actions;

export default farmerSlice.reducer;
